import React,{Component} from 'react';
import "./Tour.css";

 export default class TourList extends Component {

  state ={
    showInfo:false
  }
     render() {
       const {country,img,name,info}=this.props.tour;
         return (
       <article className="tour">
       <div className="img-container">
       <img 
       width="200"
       src="https://images.pexels.com/photos/894443/pexels-photo-894443.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" alt=""></img>
     
       <span className="close-btn">
       <i className="fa fa-window-close-o" />
       </span>
       </div>
       <div className="tour-info">
       <h3>{country}</h3>
       <h4>{name}</h4>
       <h5>info <span> <i className="fa fa-envelope-open" /></span></h5>
       
       {this.state.showInfo && <p>{info}</p>}
   
     
       </div>
       </article>
         );
     }
 }
