import React,{Component} from 'react';
import "./Tour.css";

 export default class TourList extends Component {

  state ={
    showInfo:false
  };
  handleInfo=()=>{
    this.setState({
      showInfo:!this.state.showInfo
    })
  }
     render() {
       const {id,country,img,name,info}=this.props.tour;
       const{removeTour}=this.props;
         return (
       <article className="tour">
       <div className="img-container">
       <img 
       width="200"
       src="https://images.pexels.com/photos/2918521/pexels-photo-2918521.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
       alt="" 
       />
       
     
       <span className="close-btn"
       onClick={()=>{
         removeTour(id);
        }} 
       >
       <i className="fa fa-window-close-o" />
       </span>
       </div>
       <div className="tour-info">
       <h3>{country}</h3>
       <h4>{name}</h4>
       <h5>info <span onClick={this.handleInfo}> <i className="fa fa-envelope-open" /></span></h5>
       
       {this.state.showInfo && <p>{info}</p>}
   
     
       </div>
       </article>
         );
     }
 }
