import React,{useState,useEffect} from 'react'
import './App.css'
import Post from './Post'
import {db} from './firebase'
function App ()  {
    const[posts,setPosts]=useState([
       /*
        {
            username:"picasa", 
            caption:"Wow its wonderful" ,
            imageUrl:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTbAmafdPNr9fd0KC0Z98WYEC7Wl1wYlPVf-A&usqp=CAU"
        },
        {
            username:"picasa", 
            caption:"Wow its wonderful" ,
            imageUrl:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTbAmafdPNr9fd0KC0Z98WYEC7Wl1wYlPVf-A&usqp=CAU"
        },
        */
        
    ]);


    useEffect(()=>{
          db.collection('posts').onSnapshot(snapshot=>{
              setPosts(snapshot.docs.map(doc=>doc.data()))
          });
    },[])
   
    return (
        <div className="app">
        <div className="app_header">
            <img className="app_headerImage"
            src="https://cdn.worldvectorlogo.com/logos/instagram-1.svg"
            >
            </img>
        </div>
        {
            posts.map(post=>(
                <Post username={post.username} caption={post.caption} imageUrl={post.imageUrl}></Post>
            ))
        }
      
       
        </div>
    )
}

export default App
